function displayMenu(cid){
var count = $(".ulsub").size();
if(count>2){
var tcid = "ul_"+cid;
	$(".ulsub").each(function(index){
		count++;

		//ul_Common means common taks such as change password, change secret question, view academic council resulation
		if(this.id!="ul_Common" && this.id!=tcid){
			var sid = this.id.split("_")[1];
			var html = $("#h_"+sid).val();
			$("#a_"+sid).html("[+] "+html);			
			$(this).hide();
       }
	});
    
	$("#ul_"+cid).toggle();
	var html = $("#h_"+cid).val();
	var dis = $("#ul_"+cid).css("display");
	if(dis=='block')
		$("#a_"+cid).html("[-] "+html);
	else
		$("#a_"+cid).html("[+] "+html);	
	setCookie('maingroup',cid,5);
  }
}

function setMenu(cid){
var count = $(".ulsub").size();
var tcid = "ul_"+cid;
	$(".ulsub").each(function(index){

		if(this.id!="ul_Common" && this.id!=tcid){
			var sid = this.id.split("_")[1];
			var html = $("#h_"+sid).val();
			$("#a_"+sid).html("[+] "+html);			
			$(this).hide();
       }
	});

	if(count>2){
		$("#ul_"+cid).show();
		var dis = $("#ul_"+cid).css("display");
		var html = $("#h_"+cid).val();
		if(dis=='block')
			$("#a_"+cid).html("[-] "+html);
		else
			$("#a_"+cid).html("[+] "+html);
    }	

}


function setCookie(c_name,value,expiredays)
{
	var exdate=new Date();
	exdate.setDate(exdate.getDate()+expiredays);
	document.cookie=c_name+ "=" +escape(value)+
		((expiredays==null) ? "" : ";expires="+exdate.toGMTString());
}

function getCookie(c_name)
{
if (document.cookie.length>0)
  {
  c_start=document.cookie.indexOf(c_name + "=");
  if (c_start!=-1)
    {
    c_start=c_start + c_name.length+1;
    c_end=document.cookie.indexOf(";",c_start);
    if (c_end==-1) c_end=document.cookie.length;
    return unescape(document.cookie.substring(c_start,c_end));
    }
  }
return "";
}
</script>

